import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, Linking } from 'react-native'


export default function Home() {
    const [objUser, setObjUser] = useState({})
    const getMoviesFromApi = () => {
        return fetch('https://api.github.com/users/witillan')
            .then((resposta) => resposta.json())
            .then((r) => {
                setObjUser(r)
                return r
            })
            .catch((error) => {
                alert(`Ocorreu um erro ${error.text()} ao buscar o usuário na API do "Github"`);
            })
    };

    useEffect(() => {
        getMoviesFromApi()
    }, [])

    return (
        <View style={styles.container}>
            <View style={styles.card}>
                <Text style={[styles.text, { fontSize: '2vw' }]}>Informações do Usuário</Text>
                <Text style={[styles.text, { marginTop: 20 }]}>Usuário: {objUser.name}</Text>
                <Text
                    style={[styles.link, { padding: 2, }]}
                    onPress={() => {
                        Linking.openURL(objUser.html_url.toString());
                    }}>
                    Ir para o Repositório do Usuário
                </Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center'
    },
    card: {
        borderRadius: 5,
        padding: 20,
        backgroundColor: 'green',
        shadowColor: '#000000CC',
        alignItems: 'center',
        justifyContent: 'center'
    },
    text: {
        color: 'white'
    },
    link: {
        "&:hover": {
            background: "#efefef"
        },
        "&:last-child": {
            borderRight: "solid 1px #cccccc"
        }
    }
});
