import React, { useEffect, useState } from 'react';
import { Button, FlatList, Linking, SafeAreaView, StyleSheet, Text, View } from 'react-native';

import Home from './Home';


export default function ListagemUsuarios() {
    const [listUsers, setListUsers] = useState({})
    const getUsers = () => {
        return fetch('https://api.github.com/users')
            .then((resposta) => resposta.json())
            .then((r) => {
                setListUsers(r)
                return r
            })
            .catch((error) => {
                alert(`Ocorreu um erro ${error.text()} ao buscar os usuários na API do "Github"`);
            })
    }

    const goBack = () => {
        return (
            <Home />
        )
    }

    useEffect(() => {
        getUsers()
    }, [])

    const Item = ({ item }) => {
        return (
            <View style={styles.card}>
                <View style={[styles.viewText]}>
                    <img alt='image_user' style={{ width: 70, borderRadius: '100%' }} src={`${item.avatar_url}.png`} />
                    <Text style={styles.text}>{item.name}</Text>
                    <Text style={styles.text}>{item.public_repos}</Text>
                </View>
                <Text
                    style={[styles.text, styles.link, { padding: 2, }]}
                    onPress={() => {
                        Linking.openURL(item.html_url.toString());
                    }}>
                    Link Repositório
                </Text>
            </View>
        )
    }

    return (
        <SafeAreaView style={styles.container}>
            <FlatList
                data={listUsers}
                renderItem={({ item }) => (
                    <Item item={item} />
                )}
                keyExtractor={item => item.id}
            />
            <Button title='Voltar' onPress={() => goBack()} />
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center'
    },
    card: {
        borderRadius: 5,
        padding: 20,
        backgroundColor: 'green',
        shadowColor: '#000000CC',
        alignItems: 'center',
        justifyContent: 'center'
    },
    text: {
        fontSize: 15,
        color: 'white'
    },
    viewText: {
        flexDirection: 'row',
    },
    link: {
        color: '#191970'
    }
});
